import React from "react";
import { useUser, SignedIn, SignedOut, SignInButton, SignUpButton } from "@clerk/clerk-react";
import { useNavigate } from "react-router-dom";
import "./Landing.css";

const Landing = () => {
  const navigate = useNavigate();
  const { user } = useUser();

  return (
    <div className="landing-container">
      <div className="landing-content">
        <h1>Welcome to AskSenior ðŸ‘‹</h1>
        <p>
          Connect with current college students to get real insights before choosing your college path. Whether you're
          preparing for CET, JEE, NEET or NIMCET, we've got your back!
        </p>

        <SignedOut>
          <div className="auth-buttons">
            <SignInButton mode="modal">
              <button className="btn primary">Sign In</button>
            </SignInButton>
            <SignUpButton mode="modal">
              <button className="btn secondary">Sign Up</button>
            </SignUpButton>
          </div>
        </SignedOut>

        <SignedIn>
          <p className="welcome-text">Hello, {user?.firstName || "User"} ðŸ‘‹</p>
          <button className="btn dashboard-btn" onClick={() => navigate("/profile")}>
            Go to Profile
          </button>
        </SignedIn>
      </div>
    </div>
  );
};

export default Landing;
